void Main()
{
    // Day 1 - Part 1
    var tests1 = new List<Tuple<string, int>> {
        new Tuple<string, int>("1122", 3),
        new Tuple<string, int>("1111", 4),
        new Tuple<string, int>("1234", 0),
        new Tuple<string, int>("91212129", 9),
        new Tuple<string, int>(@"", 1171)
    };

    foreach(var test in tests1) {
        var result = SumDigits(test.Item1, GetNextIndex);
        Debug.Assert(result == test.Item2, $"Input:{test.Item1} Expected:{test.Item2} Actual:{result}");
    }
   
    // Day 2 - Part 2
    var tests2 = new List<Tuple<string, int>> {
        new Tuple<string, int>("1212", 6),
        new Tuple<string, int>("1221", 0),
        new Tuple<string, int>("123425", 4),
        new Tuple<string, int>("123123", 12),
        new Tuple<string, int>("12131415", 4),
        new Tuple<string, int>(@"", 1024)
    };
   
    foreach(var test in tests2) {
        var result = SumDigits(test.Item1, GetHalfwayAroundIndex);
        Debug.Assert(result == test.Item2, $"Input:{test.Item1} Expected:{test.Item2} Actual:{result}");
    }
}

int SumDigits(string input, Func<int, int, int> getNextIndex) {
    int sum = 0;
    for(var i = 0; i < input.Length; i++) {
        var nextIndex = getNextIndex(i, input.Length);
        if (input[i] == input[nextIndex]) {
            sum += input[i] - 48;
        }
    }
    return sum;
}

int GetNextIndex(int currentIndex, int inputLength) {
    var nextIndex = (currentIndex+1) % inputLength;
    return nextIndex;
}

int GetHalfwayAroundIndex(int currentIndex, int inputLength) {
    var index = (currentIndex + inputLength/2) % inputLength;
    return index;
}
